import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
// import {reg} from '../images/login vector/730_generated.jpg'
import reg from './loginPic.jpg';
const Login =()=>{
  const [email,setEmail]= useState('');
  const [pass,setPass]= useState('');
  const loginUser= async(e)=>{
    e.preventDefault();
    const { email, password} = user;
    const res=await fetch("/login",{
      method:"POST",
      headers:{
        "Content-type":"application/json"
      },
      body: JSON.stringify({
         email, password

      })
       });
       const data =  await res.json();
       if(data.status===422 || !data){
         window.alert("Registration failed")
       }
       else{
        window.alert("Registration success");
        history.push("/login")
       }
  }
    return(
        <>
        <section className="signin">
            <div className="container mt-5">
            <div className="signup-content">
           
            <div className="signup-form">
                <h2>Sign in</h2>
                

<div class="input-group mb-3">
  <input type="email" class="form-control" value={email} onChange={(e)=>setEmail(e.target.value)} placeholder="Your email" aria-label="Recipient's username" aria-describedby="basic-addon2"/>
 
</div>

<div class="input-group mb-3">
  <input type="password" class="form-control" value={pass} onChange={(e)=>setPass(e.target.value)} placeholder="Password" aria-label="Username"/>
  </div>
  
<div className="register-button">
<button type="button" class="btn btn-primary" onClick={loginUser}>Sign in!</button>
</div>
<NavLink to="/signup" className="already">Create an account </NavLink>

                </div>
                <div className="Regpic">
            <img src={reg} alt="login"/>
            </div>
            </div>
            
            </div>
            
        </section>
        </>
    )
}
export default Login;
